import React from 'react'

class User extends React.Component {
  onRemove = () => {
    const {
       item,
       index,
       onRemoved
     } = this.props
    onRemoved(item, index)
  }

  onSelect = () => {
    const {
      item,
      onSelected
     } = this.props
    if (!item.active) {
      onSelected(item.id)
    }
  }

  render() {
    const {
      active,
      fullName
    } = this.props.item
    return (
      <div className={active ? "bg-secondary text-white" : null }>
        <p onClick={this.onSelect}>
          {this.props.item.fullName}
        </p>
      </div>
    )
  }
}

const PeopleGroup = ({ title, items, onRemoved, onSelected }) => (
  <div>
    <h3>{title}</h3>
    <div>{
      items.map((user, index) =>
        <User
          key={index}
          item={user}
          index={index}
          onRemoved={onRemoved}
          onSelected={onSelected}
        />
      )}
    </div>
  </div>
)

export default PeopleGroup
