import fetch from 'cross-fetch';
import {
  getMe,
  getUsers,
  getRooms,
  dummy,
  getChat
} from './backend'

const requests = {
  get: url =>
    fetch(url).then(response => response.body),

  post: url =>
    fetch(url, {
      method: 'POST'
    }).then(response => response.body),

  delete: url =>
    fetch(url, {
      method: 'DELETE'
    }).then(response => response.body)
}

export const Users = {
  getMe: () => getMe(),
  delete: (user) => dummy('deleting user')
}

export const People = {
  getPeople: () => getUsers()
}

export const Rooms = {
  getRooms: () => getRooms(),
  getChat: id => getChat(id),
  delete: room => dummy('deleting room')
}
